root = Tk()
# main window
root.geometry('550x300')
root.configure(background='#F6F6F6')
root.title('Spanish Spelling Bee')

word = tk.StringVar()

# button click function for advanced vocab
def btnClickFunctionAdv():
    word.set(str(random.choice(AdvancedVocab)))
    #label.config(text=word)


# button click function for basic vocab
def btnClickFunctionBasic():
    word.set(str(random.choice(BasicVocab)))
    #label.config(text=word)


# basic vocab button
Button(root, text='Basic Vocab', bg='#FFFFFF', font=('arial', 20, 'normal'), command=btnClickFunctionBasic).place(relx=0.1, y=62)

# advanced vocab button
Button(root, text='Adv Vocab', bg='#FFFFFF', font=('arial', 20, 'normal'), command=btnClickFunctionAdv).place(relx=0.6, y=62)

# label
Label(root, textvariable=word, bg='#F6F6F6', font=('arial', 40, 'normal')).place(relx=0.5,rely=0.6,anchor=CENTER)
root.mainloop()
